import soapUI.*
import java.text.SimpleDateFormat

//get date base for variables
def today = new Date()
newPerson = new SimpleDateFormat("MMddHHmmssSS")

newRequestIDBase = new SimpleDateFormat("YYYYMMdd");
newTransactionID = new SimpleDateFormat("YYYYMMddHHmmssSS");
newYear = new SimpleDateFormat("YYYY");
newMonth = new SimpleDateFormat("MM");
newDay = new SimpleDateFormat("dd");
newDollarA = new SimpleDateFormat("dd");
newDollarB = new SimpleDateFormat("ss");
newDollarConcatenated = newDollarA.format(today)+"."+newDollarB.format(today);

//set the matterNumber aProperties with the new info returned from SoapUITestData
testRunner.testCase.testSuite.project.setPropertyValue( "RequestID", newRequestIDBase.format(today));
testRunner.testCase.testSuite.project.setPropertyValue( "TransactionID", newTransactionID.format(today));
testRunner.testCase.testSuite.project.setPropertyValue( "Amount", newDollarConcatenated);
testRunner.testCase.testSuite.project.setPropertyValue( "TransactionDate", newYear.format(today) + "-" + newMonth.format(today) + "-" + newDay.format(today));
testRunner.testCase.testSuite.project.setPropertyValue( "MerchantID", "SHELL" + newMonth.format(today) + newDay.format(today));
testRunner.testCase.testSuite.project.setPropertyValue( "MerchantName", "Merchant " + newMonth.format(today) + newDay.format(today));

