import java.text.SimpleDateFormat;

def groovyUtils = new com.eviware.soapui.support.GroovyUtils( context )

def testCaseEnvironment = com.eviware.soapui.SoapUI.globalProperties.getPropertyValue("basehost");
def password_staging = testRunner.testCase.testSuite.project.getPropertyValue("StagingPassword");
def password_qa = testRunner.testCase.testSuite.project.getPropertyValue("QAPassword");
def password_prod = testRunner.testCase.testSuite.project.getPropertyValue("ProductionPassword");

//determine which password to use based on environment setting
switch (testCaseEnvironment){
	case "api.chromeriver.com":
		password = password_prod
		break
	case "qa-api.chromeriver.com":
		password = password_qa
		break
	case "staging-api.chromeriver.com":
		password = password_staging
		break
	default:
		password = "default"
}
def today = new Date();
testRunner.testCase.testSuite.project.setPropertyValue("LoginPassword",password);
def newID = new SimpleDateFormat("MddHHmmssSS");
def transactionID = newID.format(today);
testRunner.testCase.testSuite.project.setPropertyValue("TransactionID",transactionID);
